function addImage($path, $title)
{
    global $wpdb;
    $wpdb->insert(
        'image',
        array(
            'path' => $path,
            'title' => $title,
            'public' => 1
        ),
        array(
            '%s',
            '%s',
            '%d'
        )
    );
    $imageID = $wpdb->insert_id;
    return $imageID;
}

function updateImage($imageID, $public)
{
    global $wpdb;
    $wpdb->update(
        'image',
        array(
            'public' => $public // integer (number)
        ),
        array('id' => $imageID),
        array(
            '%d' // value2
        ),
        array('%d')
    );
    return true;
}

function updateDataImage($imageID, $path, $title)
{
    global $wpdb;
    $wpdb->update(
        'image',
        array(
            'path' => $path,
            'title' => $title
        ),
        array('id' => $imageID),
        array(
            '%s',
            '%s',
        ),
        array('%d')
    );
    return true;
}

function getIDImageFromUrl($imageUrl)
{
    global $wpdb;
    // We need to get the image's meta ID..
    $query = "SELECT ID FROM wp_posts where guid = '" . esc_url($imageUrl) . "' AND post_type = 'attachment'";
    $results = $wpdb->get_results($query);

    // And delete them (if more than one attachment is in the Library
    foreach ($results as $row) {
        return $row->ID;
    }
    return false;
}

function deleteImage($imageID)
{
    global $wpdb;
    $strSql = "
        DELETE
        FROM `image`
        WHERE `id` = $imageID
    ";
    $wpdb->query($strSql);
    return true;
}

