        } else {
            loadTab("tab1");
            $jConflict("#tab1").addClass("tap-current");
        }
    }

    function loadTab(tabID) {
        $jConflict('.tabscontent').load("<?php echo network_site_url('/');?>?tabPage=" + tabID).hide().fadeIn(800);
